import React, { useState } from 'react';
import './Signup.module.css';

const Signup = () => {
  // States for the form fields
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [dob, setDob] = useState("");
  const [gender, setGender] = useState("");
  const [nationality, setNationality] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [address, setAddress] = useState("");
  const [govtId, setGovtId] = useState("");
  const [occupation, setOccupation] = useState("");
  const [income, setIncome] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [branchId, setBranchId] = useState(""); // State for the selected branch
  const [branchName, setBranchName] = useState(""); // State for the branch name
  const [errors, setErrors] = useState({});

  // List of Indian cities to use as branch names
  const cities = [
    { id: 1, name: 'Mumbai' },
    { id: 2, name: 'Delhi' },
    { id: 3, name: 'Bangalore' },
    { id: 4, name: 'Chennai' },
    { id: 5, name: 'Kolkata' },
    { id: 6, name: 'Hyderabad' },
    { id: 7, name: 'Pune' },
    { id: 8, name: 'Ahmedabad' },
    { id: 9, name: 'Surat' },
    { id: 10, name: 'Jaipur' }
  ];

  const validateForm = () => {
    const newErrors = {};
    if (!firstName) newErrors.firstName = "First name is required";
    if (!lastName) newErrors.lastName = "Last name is required";
    if (!dob) newErrors.dob = "Date of birth is required";
    if (!email) newErrors.email = "Email is required";
    if (!password) newErrors.password = "Password is required";
    if (password !== confirmPassword) newErrors.confirmPassword = "Passwords do not match";
    if (!branchId) newErrors.branchId = "Branch selection is required";
    return newErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formErrors = validateForm();
  
    if (Object.keys(formErrors).length === 0) {
      try {
        const response = await fetch('http://localhost:5000/api/auth/signup', {
          method: "POST",
          headers: {
            "Content-Type": "application/json"
          },
          body: JSON.stringify({
            firstName,
            lastName,
            dob,
            gender,
            nationality,
            phone,
            email,
            address,
            govtId,
            occupation,
            income,
            password,
            branchId, // Send the selected branch ID in the request body
            branchName // Send the selected branch name
          })
        });
  
        if (!response.ok) throw new Error("Failed to sign up");
  
        const data = await response.json();
        console.log("Signup successful:", data);
  
        // Save user info in cookies (optional, if you plan to use these later)
        document.cookie = `email=${data.email}; path=/`;
        document.cookie = `balance=${data.balance}; path=/`;
        document.cookie = `firstName=${data.firstName}; path=/`;
  
        // Redirect to dashboard
        window.location.href = "/dashboard";
  
      } catch (err) {
        console.error("Signup error:", err);
        alert("Signup failed: " + err.message);
      }
    } else {
      setErrors(formErrors);
    }
  };

  return (
    <div className="signup-container">
      <h1>Create an Account</h1>
      <p className="subtitle">Please fill in the form below to register</p>

      <form onSubmit={handleSubmit} className="signup-form">
        <div className="form-section">
          <h2>Personal Information</h2>

          {/* First Name */}
          <div className="form-row">
            <label htmlFor="firstName">First Name</label>
            <input
              type="text"
              id="firstName"
              value={firstName}
              onChange={(e) => setFirstName(e.target.value)}
              required
            />
            {errors.firstName && <span className="error-message">{errors.firstName}</span>}
          </div>

          {/* Last Name */}
          <div className="form-row">
            <label htmlFor="lastName">Last Name</label>
            <input
              type="text"
              id="lastName"
              value={lastName}
              onChange={(e) => setLastName(e.target.value)}
              required
            />
            {errors.lastName && <span className="error-message">{errors.lastName}</span>}
          </div>

          {/* Date of Birth */}
          <div className="form-row">
            <label htmlFor="dob">Date of Birth</label>
            <input
              type="date"
              id="dob"
              value={dob}
              onChange={(e) => setDob(e.target.value)}
              required
            />
            {errors.dob && <span className="error-message">{errors.dob}</span>}
          </div>

          {/* Branch Selection */}
          <div className="form-row">
            <label htmlFor="branchId">Branch</label>
            <select
              id="branchId"
              value={branchId}
              onChange={(e) => {
                const selectedBranchId = e.target.value;
                setBranchId(selectedBranchId);
                setBranchName(e.target.options[e.target.selectedIndex].text);
              }}
              required
            >
              <option value="">Select Branch</option>
              {cities.map((city) => (
                <option key={city.id} value={city.id}>
                  {city.name}
                </option>
              ))}
            </select>
            {errors.branchId && <span className="error-message">{errors.branchId}</span>}
          </div>

          {/* Password and Confirm Password */}
          <div className="form-row">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            {errors.password && <span className="error-message">{errors.password}</span>}
          </div>

          <div className="form-row">
            <label htmlFor="confirmPassword">Confirm Password</label>
            <input
              type="password"
              id="confirmPassword"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              required
            />
            {errors.confirmPassword && <span className="error-message">{errors.confirmPassword}</span>}
          </div>

          {/* Submit Button */}
          <div className="form-row">
            <button type="submit">Sign Up</button>
          </div>
        </div>
      </form>
    </div>
  );
};

export default Signup;
