const express = require('express');
const jwt = require('jsonwebtoken');
const oracledb = require('oracledb');
const Cookies = require('js-cookie');

// Middleware for token validation
const authenticateToken = (req, res, next) => {
  const token = req.header('Authorization').replace('Bearer ', '');
  if (!token) return res.status(401).send('Access Denied');

  jwt.verify(token, 'your_jwt_secret_key', (err, user) => {
    if (err) return res.status(403).send('Invalid Token');
    req.user = user;
    next();
  });
};

// Recharge API endpoint
app.post('/api/recharge/process', authenticateToken, async (req, res) => {
  const { mobileNumber, operator, circle, amount, plan } = req.body;

  // Get email from cookies (this should be set on the frontend during login)
  const email = req.cookies.email;

  if (!email) {
    return res.status(401).json({ message: 'User not authenticated. Please login again.' });
  }

  let connection;

  try {
    // Connect to the database
    connection = await oracledb.getConnection(dbConfig);

    // 1. Fetch user ID based on email from the `users` table
    const userResult = await connection.execute(
      `SELECT ID FROM users WHERE EMAIL = :email`,
      [email],
      { outFormat: oracledb.OUT_FORMAT_OBJECT }
    );

    if (userResult.rows.length === 0) {
      return res.status(404).json({ message: 'User not found.' });
    }

    const userId = userResult.rows[0].ID;

    // 2. Fetch balance from the `accounts` table based on user ID
    const accountResult = await connection.execute(
      `SELECT BALANCE FROM accounts WHERE USER_ID = :userId`,
      [userId],
      { outFormat: oracledb.OUT_FORMAT_OBJECT }
    );

    if (accountResult.rows.length === 0) {
      return res.status(404).json({ message: 'Account not found.' });
    }

    const currentBalance = accountResult.rows[0].BALANCE;

    // 3. Check if user has enough balance
    if (currentBalance < amount) {
      return res.status(400).json({ message: 'Insufficient balance!' });
    }

    // 4. Deduct the recharge amount from the balance
    const updatedBalance = currentBalance - amount;

    // 5. Update the balance in the `accounts` table
    await connection.execute(
      `UPDATE accounts SET BALANCE = :updatedBalance WHERE USER_ID = :userId`,
      [updatedBalance, userId]
    );

    // Commit the transaction
    await connection.commit();

    // Send a success response
    res.json({
      message: 'Recharge successful!',
      updatedBalance: updatedBalance
    });

  } catch (err) {
    console.error('Error during recharge:', err);
    res.status(500).json({ message: 'Server error. Please try again later.' });
  } finally {
    if (connection) {
      try {
        // Close the database connection
        await connection.close();
      } catch (err) {
        console.error('Error closing connection:', err);
      }
    }
  }
});

// Start the server
app.listen(5000, () => {
  console.log('Server is running on port 5000');
});
