import React, { useState } from 'react';
import './Recharge.css';
import axios from 'axios';
import Cookies from 'js-cookie';

const Recharge = () => {
  const [formData, setFormData] = useState({
    mobileNumber: '',
    operator: '',
    circle: '',
    amount: '',
    plan: 'custom'
  });

  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const operators = [
    { id: 'jio', name: 'Reliance Jio' },
    { id: 'airtel', name: 'Airtel' },
    { id: 'vi', name: 'Vi (Vodafone Idea)' },
    { id: 'bsnl', name: 'BSNL' },
    { id: 'mtnl', name: 'MTNL' }
  ];

  const circles = [
    { id: 'andhra-pradesh', name: 'Andhra Pradesh' },
    { id: 'assam', name: 'Assam' },
    { id: 'bihar', name: 'Bihar & Jharkhand' },
    { id: 'delhi', name: 'Delhi NCR' },
    { id: 'gujarat', name: 'Gujarat' },
    // add other circles...
  ];

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSuccessMessage('');
    setErrorMessage('');

    // Validate amount
    if (parseInt(formData.amount) <= 0) {
      setErrorMessage('Please enter a valid amount for recharge.');
      return;
    }

    try {
      const userEmail = Cookies.get('email');
      const userBalance = parseInt(Cookies.get('balance'));

      if (userBalance < parseInt(formData.amount)) {
        setErrorMessage('Insufficient balance!');
        return;
      }

      // Sending request to the backend
      const response = await axios.post('http://localhost:5000/api/transaction/process', {
        email: userEmail,
        mobileNumber: formData.mobileNumber,
        operator: formData.operator,
        circle: formData.circle,
        amount: formData.amount,
        transactionType: 'recharge'
      });

      const updatedBalance = response.data.updatedBalance;

      Cookies.set('balance', updatedBalance);

      setSuccessMessage('Recharge successful! Your new balance is ₹' + updatedBalance);

      setFormData({
        mobileNumber: '',
        operator: '',
        circle: '',
        amount: '',
        plan: 'custom'
      });
    } catch (error) {
      console.error('Error:', error);
      setErrorMessage('Recharge failed. Please try again later.');
    }
  };

  return (
    <div className="recharge-container">
      <h1>Mobile Recharge</h1>
      <form onSubmit={handleSubmit} className="recharge-form">
        <div className="form-group">
          <label htmlFor="mobileNumber">Mobile Number</label>
          <input
            type="tel"
            id="mobileNumber"
            name="mobileNumber"
            value={formData.mobileNumber}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="operator">Select Operator</label>
          <select
            id="operator"
            name="operator"
            value={formData.operator}
            onChange={handleInputChange}
            required
          >
            <option value="">Select your operator</option>
            {operators.map((operator) => (
              <option key={operator.id} value={operator.id}>
                {operator.name}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="circle">Select Circle</label>
          <select
            id="circle"
            name="circle"
            value={formData.circle}
            onChange={handleInputChange}
            required
          >
            <option value="">Select your circle</option>
            {circles.map((circle) => (
              <option key={circle.id} value={circle.id}>
                {circle.name}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="amount">Amount (₹)</label>
          <input
            type="number"
            id="amount"
            name="amount"
            value={formData.amount}
            onChange={handleInputChange}
            min="10"
            required
          />
        </div>

        <button type="submit">Proceed to Recharge</button>
      </form>

      {successMessage && <p>{successMessage}</p>}
      {errorMessage && <p>{errorMessage}</p>}
    </div>
  );
};

export default Recharge;
