import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./Dashboard.css";

const Dashboard = () => {
  const [balance] = useState(1200.50); // Example balance

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h2 className="dashboard-title">Welcome Back</h2>
        <p className="dashboard-balance">
          Balance: <span className="balance-amount">${balance.toFixed(2)}</span>
        </p>
      </div>

      <div className="dashboard-buttons">
        {/* <Link to="/recharge"> */}
          <button className="dashboard-button recharge-button">Recharge</button>
        {/* </Link> */}
        {/* <Link to="/transfer"> */}
          <button className="dashboard-button transfer-button">Transfer</button>
        {/* </Link> */}
        {/* <Link to="/payment-history"> */}
          <button className="dashboard-button payment-history-button">
            Payment History
          </button>
        {/* </Link> */}
      </div>
    </div>
  );
};

export default Dashboard;
