// src/pages/Login.js
import React, { useState } from "react";
import { Link, useHistory } from "react-router-dom"; // Import useHistory for redirection
import axios from "axios"; // For making the API request
import "./Login.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null);
  const history = useHistory(); // For redirecting the user upon successful login

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      // Sending login request to the backend
      const response = await axios.post(
        "/api/login", // Make sure this matches the backend route
        { email, password },
        { withCredentials: true } // Sending cookies along with the request
      );

      // If login is successful, redirect to dashboard (or other page)
      console.log("Login successful:", response.data);
      history.push("/dashboard"); // Redirect to dashboard
    } catch (err) {
      console.error("Login error:", err);
      setError("Invalid email or password"); // Set error message if login fails
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <form className="login-form" onSubmit={handleLogin}>
        <input 
          type="email" 
          placeholder="Email" 
          value={email} 
          onChange={(e) => setEmail(e.target.value)} 
          required 
        />
        <input 
          type="password" 
          placeholder="Password" 
          value={password} 
          onChange={(e) => setPassword(e.target.value)} 
          required 
        />
        {error && <div className="error-message">{error}</div>} {/* Display error if any */}
        <button type="submit">Login</button>
      </form>
      <p>Don't have an account? <Link to="/signup">Sign up</Link></p>
    </div>
  );
};

export default Login;
