import React, { useState, useEffect } from 'react';
import './Dashboard.css';
import { Link } from 'react-router-dom';
import Cookies from 'js-cookie';

const Dashboard = () => {
  const [displayBalance, setDisplayBalance] = useState(0);
  const [actualBalance, setActualBalance] = useState(parseFloat(Cookies.get('balance')) || 0);
  const firstName = Cookies.get('firstName') || "User";

  // Function to fetch balance from the server
  const fetchBalance = async () => {
    try {
      console.log('Fetching balance from server...');
      const response = await fetch('/api/recharge/getBalance', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        }
      });

      if (response.ok) {
        const data = await response.json();
        console.log('Balance fetched:', data);

        if (data.balance !== undefined) {
          setActualBalance(data.balance);
          Cookies.set('balance', data.balance);  // Update the balance in cookies
        }
      } else {
        console.error('Failed to fetch balance');
      }
    } catch (error) {
      console.error('Error fetching balance:', error);
    }
  };

  useEffect(() => {
    // Fetch balance every 5 seconds (or adjust the interval as needed)
    const intervalId = setInterval(() => {
      fetchBalance();
    }, 5000);  // Fetch balance every 5 seconds

    // Fetch balance initially
    fetchBalance();

    return () => clearInterval(intervalId);  // Cleanup on unmount
  }, []);

  useEffect(() => {
    console.log('Balance updated in state:', actualBalance);
    
    const duration = 2000;  // Duration for balance transition
    const stepTime = 10;    // Time interval for each step
    const step = actualBalance / (duration / stepTime);  // Calculate how much to increment the balance by

    const interval = setInterval(() => {
      setDisplayBalance(prev => {
        if (prev < actualBalance) {
          return Math.min(prev + step, actualBalance);
        } else {
          clearInterval(interval);
          return actualBalance;
        }
      });
    }, stepTime);

    return () => clearInterval(interval);  // Cleanup interval
  }, [actualBalance]);  // Only re-run when `actualBalance` changes

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h2 className="dashboard-title">Welcome, {firstName} ðŸ‘‹</h2>
        <div className="dashboard-balance">
          <p>Balance</p>
          <p className="balance-amount">â‚¹{displayBalance.toFixed(2)}</p>
        </div>
      </div>

      <div className="dashboard-buttons">
        <Link to="/recharge" className="dashboard-button recharge-button">
          Recharge
        </Link>
        <Link to="/transfer" className="dashboard-button transfer-button">
          Transfer
        </Link>
        <Link to="/transactions" className="dashboard-button payment-history-button">
          Payment History
        </Link>
      </div>
    </div>
  );
};

export default Dashboard;
