import React, { useState, useEffect } from 'react';
import './Dashboard.css';
import { Link } from 'react-router-dom';

const Dashboard = () => {
  // Initial balance set to 0, and target balance as an example
  const [balance, setBalance] = useState(0);
  const targetBalance = 5000;  // Example target balance

  useEffect(() => {
    const duration = 2000; // Duration for the animation in milliseconds (2 seconds)
    const stepTime = 10; // Interval for updating the number (in milliseconds)
    const step = targetBalance / (duration / stepTime); // How much to increment each step

    const interval = setInterval(() => {
      setBalance(prev => {
        if (prev < targetBalance) {
          return Math.min(prev + step, targetBalance); // Ensure balance doesn't exceed the target
        } else {
          clearInterval(interval); // Stop interval when target is reached
          return targetBalance;
        }
      });
    }, stepTime);

    // Cleanup the interval when the component unmounts or balance changes
    return () => clearInterval(interval);
  }, [targetBalance]);

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h2 className="dashboard-title">Welcome</h2>
        <div className="dashboard-balance">
          <p>Balance</p>
          <p className="balance-amount">â‚¹{balance.toFixed(2)}</p>
        </div>
      </div>
      <div className="dashboard-buttons">
        <Link to="/recharge">
        <button className="dashboard-button recharge-button">Recharge</button>
        </Link>
        <button className="dashboard-button transfer-button">Transfer</button>
        <button className="dashboard-button payment-history-button">Payment History</button>
      </div>
    </div>
  );
};

export default Dashboard;
