import React, { useState, useEffect } from 'react';
import './Dashboard.css';
import { Link } from 'react-router-dom';
import Cookies from 'js-cookie';

const Dashboard = () => {
  const firstName = Cookies.get('firstName') || "User";
  const [displayBalance, setDisplayBalance] = useState(0);
  const [actualBalance, setActualBalance] = useState(parseFloat(Cookies.get('balance')) || 0);

  // Recheck the balance from cookie every 5 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      const latestBalance = parseFloat(Cookies.get('balance')) || 0;
      if (latestBalance !== actualBalance) {
        setActualBalance(latestBalance);
      }
    }, 5000); // Refresh every 5 seconds

    return () => clearInterval(interval);
  }, [actualBalance]);

  // Animate displayBalance towards actualBalance
  useEffect(() => {
    const duration = 2000;
    const stepTime = 10;
    const step = Math.abs(actualBalance - displayBalance) / (duration / stepTime);

    const interval = setInterval(() => {
      setDisplayBalance(prev => {
        if (prev < actualBalance) {
          return Math.min(prev + step, actualBalance);
        } else if (prev > actualBalance) {
          return Math.max(prev - step, actualBalance);
        } else {
          clearInterval(interval);
          return actualBalance;
        }
      });
    }, stepTime);

    return () => clearInterval(interval);
  }, [actualBalance]);

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h2 className="dashboard-title">Welcome, {firstName} ðŸ‘‹</h2>
        <div className="dashboard-balance">
          <p>Balance</p>
          <p className="balance-amount">â‚¹{displayBalance.toFixed(2)}</p>
        </div>
      </div>

      <div className="dashboard-buttons">
        <Link to="/recharge" className="dashboard-button recharge-button">
          Recharge
        </Link>
        <Link to="/transfer" className="dashboard-button transfer-button">
          Transfer
        </Link>
        <Link to="/transactions" className="dashboard-button payment-history-button">
          Payment History
        </Link>
      </div>
    </div>
  );
};

export default Dashboard;
